         this.addField_SFBool(ctx, "enabled", 'true');
         
         /** catetory [0..255] */
         this.addField_SFInt32(ctx, "entityCategory", 0);
         
         /** country [0..16K], 225 = USA */
         this.addField_SFInt32(ctx, "entityCountry", 0);
         
