    this._11 = a[ 5 ];
    this._12 = a[ 9 ];
    this._13 = a[ 13 ];
    this._20 = a[ 2 ];
    this._21 = a[ 6 ];
    this._22 = a[ 10 ];
    this._23 = a[ 14 ];
    this._30 = a[ 3 ];
    this._31 = a[ 7 ];
    this._32 = a[ 11 ];
    this._33 = a[ 15 ];

