    shader = shader.replace( /\/\/@insertFragColor/g, "out vec4 " + x3dom.shader.FRAG_COLOR + ";\n" );

    shader = shader.replace( /gl_FragColor/g, x3dom.shader.FRAG_COLOR );

    return "#version 300 es\n" + shader;
