        var cor = view._at;

        cor = cor.addScaled( this.panAxisY, ty );
        var temp = cor;
        view._from = view._from.addScaled( this.panAxisY, ty );

        cor = temp.addScaled( this.panAxisX, tx );
        view._from = view._from.addScaled( this.panAxisX, tx );

